import { Component } from '@angular/core';
import { Produto } from './models/produto';
import { ProdutoService } from './services/produto.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent  {

  title = 'montanha-front';

  listaProduto = new Array<Produto>

  visibleGetProdutos: boolean = false
  visibleGetProduto: boolean = false
  visibleCadastro: boolean = false
  visiblePut: boolean = false
  visibleDelete: boolean = false;

  constructor(private produtoService: ProdutoService){}

  changeVisibleGetProdutos() {
    this.visibleGetProdutos = !this.visibleGetProdutos
    console.log(this.visibleGetProdutos);
  }

  changeVisibleDelete() {
    this.visibleDelete = !this.visibleDelete
    console.log(this.visibleDelete);
  }

  changeVisibleGetProduto() {
    this.visibleGetProduto = !this.visibleGetProduto
    console.log(this.visibleGetProduto);
  }

  changeVisiblePut() {
    this.visiblePut = !this.visiblePut
    console.log(this.visiblePut);
  }

  changeVisibleCadastro() {
    this.visibleCadastro = !this.visibleCadastro
    console.log(this.visibleCadastro);
  }

  retornaProdutos() {
    this.listaProduto = []
    let observable = this.produtoService.getAll();
    observable.subscribe(listaProdutos => {
      const entries = Object.entries(listaProdutos);
      entries.forEach(entry => {
        console.log(entry[1]);
        this.listaProduto.push(entry[1])
      })
    })
  }


}
<div class="content" role="main">
  <h2>Atividade CRUD</h2>

  <h2>Listagem de Produtos</h2>
  <button type="button" (click)="changeVisibleGetProdutos()">Ver produtos registrados</button>
  <app-ler-produtos *ngIf="visibleGetProdutos"></app-ler-produtos>

  <h2>Listagem de Produto especifíco</h2>
  <button type="button" (click)="changeVisibleGetProduto()">Ver produtos registrados</button>
  <app-ler-produtos *ngIf="visibleGetProduto"></app-ler-produtos>

  <h2>Cadastro de Produto</h2>
  <button type="button" (click)="changeVisibleCadastro()">Ver produtos registrados</button>
  <app-cadastro-produto *ngIf="visibleCadastro"></app-cadastro-produto>

  <h2>Update de Produto</h2>
  <button type="button" (click)="changeVisibleCadastro()">Ver produtos registrados</button>
  <app-update-produto *ngIf="visiblePut"></app-update-produto>


  <h2>Remover Produto</h2>
  <button type="button" (click)="changeVisibleDelete()">Ver produtos registrados</button>
  <app-delete-produto *ngIf="visibleDelete"></app-delete-produto>

</div>
