import { Component } from '@angular/core';
import { ProdutoService } from 'src/app/services/produto.service';

@Component({
  selector: 'app-cadastro-produto',
  templateUrl: './cadastro-produto.component.html',
  styleUrls: ['./cadastro-produto.component.css']
})
export class CadastroProdutoComponent {
  nomeProduto : string = ''
  idProduto : number = 0
  descricaoProduto : string = ''
  precoProduto : number = 0
  constructor(private produtoService: ProdutoService){}

  ngOnInit() : void {
  }

  cadastrarProduto() {
    console.log("Salvei: " + this.nomeProduto);

  }

  retornaProdutos() {
    let observable = this.produtoService.getAll();
    observable.subscribe(listaProdutos => {
      const entries = Object.entries(listaProdutos);

      console.log(listaProdutos);

      entries.forEach(entry => {
        console.log(entry);

      })
    })
  }

  retornaProduto(id: string) {
    let observable = this.produtoService.get(id);
    observable.subscribe(produto => {
      console.log(produto);
    })
  }

}
