import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Produto } from '../models/produto';

@Injectable({
  providedIn: 'root'
})
export class ProdutoService {

  constructor(private http: HttpClient) { }

  getAll() : Observable<Produto[]> {
    return this.http.get<Produto[]>('https://apiauladistribuidagustavo.azurewebsites.net/Produto/');
  }

  get(id: String): Observable<Produto>{
    return this.http.get<Produto>(`https://apiauladistribuidagustavo.azurewebsites.net/Produto/${id}`);
  }
}
