import { Component } from '@angular/core';
import { Produto } from 'src/app/models/produto';
import { ProdutoService } from 'src/app/services/produto.service';

@Component({
  selector: 'app-cadastro-produto',
  templateUrl: './cadastro-produto.component.html',
  styleUrls: ['./cadastro-produto.component.css']
})
export class CadastroProdutoComponent {
  nomeProduto : string = ''
  idProduto : number = 0
  descricaoProduto : string = ''
  precoProduto : number = 0
  idProdutoGet: string = '';
  constructor(private produtoService: ProdutoService){}

  cadastrarProduto() {
    console.log("Salvei: " + this.nomeProduto);
  }

  excluirProduto(id: string) {
    let observable = this.produtoService.delete(id);
    observable.subscribe(produto => {
      console.log(produto);
    })
  }

  criarProduto() {
    const c = new Produto()
    c.Name = 'Mouse'
    c.Id = 145
    c. Description = 'Mouse reddragon'
    c.Price= 120
    c.Quantity = 200
    let observable = this.produtoService.post(c);
    observable.subscribe(s => {
      console.log("Novo produto adicionado:" + s);
    })
  }

  atualizarProduto() {
    const c = new Produto()
    c.Name = 'Mouse'
    c. Description = 'Mouse reddragon'
    c.Price= 120
    c.Quantity = 200
    let observable = this.produtoService.put('1', c)
    observable.subscribe(s => {
      console.log(s);
    })
  }

}
