import { Component, Input, OnInit } from '@angular/core';
import { Produto } from 'src/app/models/produto';
import { ProdutoService } from 'src/app/services/produto.service';

@Component({
  selector: 'app-ler-produtos',
  templateUrl: './ler-produtos.component.html',
  styleUrls: ['./ler-produtos.component.css']
})
export class LerProdutosComponent implements OnInit{
  idProdutoGet : string = ''

  produtoUnico : any

  listaProdutos = new Array<Produto>

  constructor(private produtoService: ProdutoService){}

  ngOnInit(): void {
    this.retornaProdutos()
  }


  retornaProdutos() {
    let observable = this.produtoService.getAll();
    observable.subscribe(listaProdutos => {
      const entries = Object.entries(listaProdutos);
      console.log(listaProdutos);
      entries.forEach(entry => {
        this.listaProdutos.push(entry[1])        
      })
    })
  }

  retornaProduto(id: string) {
    let observable = this.produtoService.get(id);
    observable.subscribe(produto => {
      (this.produtoUnico = produto);
      console.log(this.produtoUnico);

    })
  }

}
<div>
    <button type="button" (click)="retornaProdutos()">Retorna produtos</button>
</div>
<div>
    <input [(ngModel)]="idProdutoGet" type="text" id="idProdutoGet" name="idProdutoGet" placeholder="Digita o id do produto">
    <button type="button" (click)="retornaProduto(idProdutoGet)">Retorna produto</button>

    <div>
    <p>Nome do produto: {{produtoUnico.name}}.</p>
    <br>
    <p>Descrição do produto: {{produtoUnico.description}}.</p>
    <br>
    <p>Preço do produto: {{produtoUnico.price}}</p>
    <br>
    <p>Quantidade de produtos em estoque: {{produtoUnico.quantity}}</p> 
    </div>
</div>
